mapper.select:
SELECT rt.id AS id, rt.relationName AS relationName, item1.id AS startItemId, item1.itemName AS startItemName, item1.itemType AS startItemType, item2.id AS endItemId, item2.itemName AS endItemName, item2.itemType AS endItemType FROM relation_type rt JOIN item item1 ON rt.startEntityItemId = item1.id JOIN item item2 ON rt.endEntityItemId = item2.id
AND rt.relationName LIKE CONCAT('%', #{relationName}, '%')
AND item1.id = #{startItemId}
AND item1.itemName LIKE CONCAT('%', #{startItemName}, '%')
AND item1.itemType = #{startItemType}
AND item2.id = #{endItemId}
AND item2.itemName LIKE CONCAT('%', #{endItemName}, '%')
AND item2.itemType = #{endItemType}
LIMIT #{offset}, #{pageSize}

SELECT
COUNT(*)
FROM
relation_type rt
JOIN item item1 ON rt.startEntityItemId = item1.id
JOIN item item2 ON rt.endEntityItemId = item2.id


        AND rt.relationName LIKE CONCAT('%', #{relationName}, '%')


        AND item1.id = #{startItemId}


        AND item1.itemName LIKE CONCAT('%', #{startItemName}, '%')


        AND item1.itemType = #{startItemType}


        AND item2.id = #{endItemId}


        AND item2.itemName LIKE CONCAT('%', #{endItemName}, '%')


        AND item2.itemType = #{endItemType}
